pragma solidity ^0.4.24;

contract Lockbox {
  event ExpiredSharedSecret(address _address, uint64 _valid_until);

  uint validity_period = 2 days;

  mapping ( address => uint256 ) shared_secrets;
  mapping ( address => uint64 ) valid_until;


  function assign_shared_secret(address _address, uint256 _shared_secret) external {
    shared_secrets[_address] = _shared_secret;
    valid_until[_address] = uint64(now + validity_period);
  }

  function get_shared_secret(address _address) external returns(uint256) {
    uint64 validity = valid_until[_address];
    if (validity < now) {
      emit ExpiredSharedSecret(_address, validity);
      return 0;
    }

    return shared_secrets[_address];
  }

  function get_validity_period(address _address) external view returns(uint64){
    return valid_until[_address];
  }


}
